import React, { useEffect, useState } from 'react';
import { Button, Form } from 'react-bootstrap';

const CommandForm = () => {
  const [inputValue, setInputValue] = useState('');
  const [robotId, setRobotId] = useState()
  console.log('inputValue', inputValue);

  useEffect(() => {
    getRobotId()
  }, [])

  const getRobotId = async () => {
    const URL = 'http://localhost:3333/robot'
    const response = await fetch(URL);
    const data = await response.json()
    await setRobotId(data[0].id)
    console.log('fetchedId', (data[0].id))
  }

  const onSubmit = async () => {
    const command = inputValue.toUpperCase()
    const id = robotId;
    console.log('data', command)
    const rawResponse = await fetch('http://localhost:3333/command', {
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({userInput: command, robotId: id}),
    })
    const content = await rawResponse.json()
    console.log(content)
  };

  return (
    <Form>
      <Form.Group className="mb-3" controlId="command">
        <Form.Label>Write a Command!</Form.Label>
        <Form.Control
          type="text"
          style={ {textTransform: 'uppercase'}}
          placeholder="Only M R L allowed!"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
        />
      </Form.Group>
      <Button 
      type="submit"
      onClick={onSubmit}>Submit</Button>
    </Form>
  );
};

export default CommandForm;
