import React from 'react';
import { Button, Form } from 'react-bootstrap';
import CommandForm from './CommandForm';
import CommandList from './CommandList';
import '../styles/shipAndGrid.css'

const Board = () => {
  const size = [1,2,3,4,5]
  const robotPos = [2, 1]
  return (
    <div className='board'>

      {
        size.map((row, x) => {
          return size.map((colum, y) =>  (robotPos[0] === x && robotPos[1] === y) ? <img src='public/spaceship.png' />: <div>robot {x} {y}</div>)
        } )
      }
    </div>
  )
}

export default Board;
