import React, { useState, useEffect} from 'react';
import CommandForm from './CommandForm';
import CommandList from './CommandList';
import ResetButton from './ResetButton';
import Board from './ShipAndGrid';


const Home = () => {
  const [haveRobot, setHaveRobot] = useState();
  const [isLoading, setIsLoading] = useState(false)

  useEffect(() => {
    loadRobot();
  }, [])

  const loadRobot = async () => {
  await setIsLoading(true)
  const getRobotURL = 'http://localhost:3333/robot'
  const response = await fetch(getRobotURL);
  const data = await response.json()
  await data ? await setHaveRobot(data) : await createRobot()
  console.log('haveRobot', haveRobot)
  console.log('data', data)
  await setIsLoading(false)
}

const createRobot = async () => {
  console.log('inicio')
    const robot = await fetch('http://localhost:3333/robot', {
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      },
      body: '',
    })
    const content = await robot.json()
    await setHaveRobot(content)

    console.log('dentro do create body final', haveRobot)
}
  if (isLoading) return <div>loading</div>
  return (
    <div>
      <header>
       <h1>Explore</h1>
       <h1>Mars</h1>
      </header>
      <CommandForm />

      <CommandList />
      <Board />
      <ResetButton />
    </div>
  );
};

export default Home;
