import CommandForm from './CommandForm';
import CommandList from './CommandList';
import ResetButton from './ResetButton';
import Board from './ShipAndGrid';
import { Button } from 'bootstrap';

const Start = () => {

  const createRobot = async () => {
    console.log('inicio')
      const robot = await fetch('http://localhost:3333/robot', {
        method: 'POST',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json',
        },
        body: '',
      })
      const content = await robot.json()

      console.log('dentro do create body final')
  }

  <Button type="submit"
  onClick={createRobot()}
  ></Button>
}
const Home = () => {
  const [haveRobotCreated, setHaveRobotCreated] = useState(1);
  const [isLoading, setIsLoading] = useState(true)

  useEffect(() => {
    if(isLoading) {
      loadRobot();

    }
  }, [])

  const tryHard = async () => {
    setHaveRobotCreated(2)
  }

  const loadRobot = async () => {
    await setIsLoading(true)
    console.log(isLoading)
    const getRobotURL = 'http://localhost:3333/robot'
    const response = await fetch(getRobotURL);
    const data = await response.json()
    await console.log('posIf', data)
    await setIsLoading(false)
}


  if (isLoading) return <div>loading</div>
  return (
    <div>
      <header>
       <h1>Explore</h1>
       <h1>Mars</h1>
      </header>
      <CommandForm />

      <CommandList />
      <Board />
      <ResetButton />
    </div>
  );
};

export default Home;
