import CommandForm from './CommandForm';
import CommandList from './CommandList';
import ResetButton from './ResetButton';
import Board from './ShipAndGrid';


const Home = () => {
  const [haveRobotCreated, setHaveRobotCreated] = useState(null);
  const [isLoading, setIsLoading] = useState(false)

  useEffect(() => {
    loadRobot();
  }, [])

  const tryHard = async () => {
    setHaveRobotCreated(true)
  }

  const loadRobot = async () => {
  try {
    await setIsLoading(true)
    console.log(isLoading)
    const getRobotURL = 'http://localhost:3333/robot'
    const response = await fetch(getRobotURL);
    const data = await response.json()
    console.log('aqui msm', data)
    if (data.length > 0) {
      console.log('dentro doif')
      await tryHard()
      console.log('apos tryhard')
    }
    await console.log('posIf', haveRobotCreated)
    await setIsLoading(false)
  } catch (err) {
    console.log('iiih')
    setHaveRobotCreated(false)
  }
}

const createRobot = async () => {
  console.log('inicio')
    const robot = await fetch('http://localhost:3333/robot', {
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      },
      body: '',
    })
    const content = await robot.json()
    await setHaveRobotCreated(content)

    console.log('dentro do create body final', haveRobotCreated)
}
  if (isLoading) return <div>loading</div>
  return (
    <div>
      <header>
       <h1>Explore</h1>
       <h1>Mars</h1>
      </header>
      <CommandForm />

      <CommandList />
      <Board />
      <ResetButton />
    </div>
  );
};

export default Home;
