import React, { useState, useEffect } from 'react';
import spaceship from '../images/spaceship.png';
import '../styles/shipAndGrid.css';

const Tile = () => {
  return <div className="tile"></div>;
};

const transformDirectionToDegree = (direction) => {
  let rotationDegree = ''
  switch (direction) {
    case 'N':
      rotationDegree = '0deg'
      break;
    case 'E':
      rotationDegree = '90deg'
      break;
    case 'S':
      rotationDegree = '180deg'
      break;
    case 'W':
      rotationDegree =  '-90deg'
    break;
    default: return rotationDegree

    return rotationDegree;
  }
}

const Board = () => {
  const [robotLocation, setRobotLocation] = useState([])



  useEffect(() => {
    loadData()
  }, [])

  const loadData = async () => {
  const URL = 'http://localhost:3333/robot'
  const response = await fetch(URL);
  const data = await response.json()
  setRobotLocation(data.current_location)
}
  const boardSize = [0, 1, 2, 3, 4];
  const reverseBoardSize = [4, 3, 2, 1, 0];
  const robotPos = robotLocation[0];
  const transform = transformDirectionToDegree(robotPos[2])
  const robotDir = {transform: `rotate(${transform})`}
  return (
    <div className="board">
      {reverseBoardSize.map((row) => {
        return boardSize.map((column) =>
          robotPos[0] === column && robotPos[1] === row ? (
            <img className="robotImg" src={spaceship} style={robotDir} />
          ) : (
            <div className="tile">
              robot {column} {row}
            </div>
          ),
        );
      })}
    </div>
  );
};

export default Board;
