import React from 'react';
import spaceship from '../images/spaceship.png'
import '../styles/shipAndGrid.css'

const Tile = () => {
  return (
    <div className='tile'></div>
  )
}

const Board = () => {
  let boardSize = [0,1,2,3,4]
  let reverseBoardSize = [4,3,2,1,0];
  const robotPos = [2, 1]
  return (
    <div className='board'>

      {
        reverseBoardSize.map((row, x) => {
          return reverseBoardSize.map((column, y) =>  (robotPos[0] === row && robotPos[1] === column) ? <img className='robotImg' src={spaceship}/>: <div>robot {row} {column}</div>)
        } )
      }
    </div>
  )
}

export default Board;
