import React, { useState, useEffect } from 'react';
import spaceship from '../images/spaceship.png';
import '../styles/shipAndGrid.css';

const Tile = () => {
  return <div className="tile"></div>;
};

const transformDirectionToDegree = async (direction) => {
  let rotationDegree = ''
  switch (direction) {
    case 'N':
      rotationDegree = '0deg'
      break;
    case 'E':
      rotationDegree = '90deg'
      break;
    case 'S':
      rotationDegree = '180deg'
      break;
    case 'W':
      rotationDegree =  '-90deg'
    break;
    default: rotationDegree = ''

    return rotationDegree;
  }
}

const parseCurrentPosition = (array) => {
  const [x, y, z] = array;
  const parsedCP = [+x, +y, z]
  return parsedCP
}


const Board = () => {
  const [robotPosition, setRobotPosition] = useState([])
  const [isLoading, setIsLoading] = useState(false)


  useEffect(() => {
    loadData();
  }, [])

  const loadData = async () => {
  await setIsLoading(true)
  const URL = 'http://localhost:3333/robot'
  const response = await fetch(URL);
  const data = await response.json()
  setRobotPosition(data[0].current_position)
  await setIsLoading(false)
  console.log('data', (data[0].current_position))
}
  const boardSize = [0, 1, 2, 3, 4];
  const reverseBoardSize = [4, 3, 2, 1, 0];
  const parsedPos = parseCurrentPosition(robotPosition)
  console.log('robotPase',parsedPos)
  const transform = transformDirectionToDegree(parsedPos[2])
  console.log(transform)
  const robotDir = {transform: `rotate(${transform})`}

  if (isLoading) return <div>loading</div>
  return (
    <div className="board">
      {reverseBoardSize.map((row) => {
        return boardSize.map((column) =>
          parsedPos[0] === column && parsedPos[1] === row ? (
            <img className="robotImg" src={spaceship} style={robotDir} />
          ) : (
            <div className="tile">
              robot {column} {row}
            </div>
          ),
        );
      })}
    </div>
  );
};

export default Board;
