import React, { useEffect, useState } from 'react';
import { Button, Form } from 'react-bootstrap';

const CommandForm = () => {
  const [inputValue, setInputValue] = useState('');
  const [robotId, setRobotId] = useState()
  console.log('inputValue', inputValue);
  useEffect(() => {

  })

  const getRobotId = async () => {
    const URL = 'http://localhost:3333/robot'
    const response = await fetch(URL);
    const data = await response.json()
    setRobotId(data[0].id)
    console.log('data', (data[0].current_position))
  }
  const onSubmit = async () => {
    const data = inputValue.toUpperCase()
    console.log('data', data)
    fetch('http://localhost:3333/command', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(inputValue.toUpperCase()),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log('Success:', data);
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  };

  return (
    <Form>
      <Form.Group className="mb-3" controlId="command">
        <Form.Label>Write a Command!</Form.Label>
        <Form.Control
          type="text"
          style={ {textTransform: 'uppercase'}}
          placeholder="Only M R L allowed!"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
        />
      </Form.Group>
      <Button 
      type="submit"
      onClick={onSubmit}>Submit</Button>
    </Form>
  );
};

export default CommandForm;
