import React from 'react';
import spaceship from '../images/spaceship.png'
import '../styles/shipAndGrid.css'

const Tile = () => {
  return (
    <div className='tile'></div>
  )
}

const Board = () => {
  let boardSize = [1,2,3,4,5]
  let reverseBoardSize = boardSize.reverse();
  const robotPos = [2, 1]
  return (
    <div className='board'>

      {
        boardSize.map((row, x) => {
          return boardSize.reverse().map((colum, y) =>  (robotPos[0] === x && robotPos[1] === y) ? <img className='robotImg' src={spaceship}/>: <div>robot {x} {y}</div>)
        } )
      }
    </div>
  )
}

export default Board;
